mapper.select:
		select id, name, model, modelId, accessory, notAccessory, payMode, secondHandCarType, secondHandCarTypeId,secondHandCarDetail, secondHandCarDetailId, tranDate, inputDate, modDate from orders where 1 = 1 
	
	     
			and name = #{name}
		 
		
            and tranDate >= #{beginDate} 
             
            
		            
            and tranDate <= #{endDate}
